import { Building } from './../../models/building';
import { Section } from './../../models/section';
import { Component, Input } from '@angular/core';
import { nf_section } from 'src/app/data/sections';

@Component({
  selector: 'app-building',
  templateUrl: './building.component.html',
  styleUrls: ['./building.component.scss']
})

export class BuildingComponent {

  @Input() building: Building

  sections: Section[] = nf_section

  showTab(evt: any, buildingName: any) {
    let i: number;
    let sections: any;
    let tablinks: any;

    sections = document.getElementsByClassName('sections');
    for(i = 0; i < sections.length; i++) {
      sections[i].style.display = 'none'
    }

    tablinks = document.getElementsByClassName('tablink');
    for(i = 0; i < tablinks.length; i++) {
      tablinks[i].className = tablinks[i].className.replace(' active', '');
    }

    document.getElementById(buildingName)!.style.display = "flex"
    evt.currentTarget!.className += ' active'
  }
}
