import { Floor } from './../../models/floor';
import { Section } from './../../models/section';
import { Component, Input } from '@angular/core';
import { nf_floor } from 'src/app/data/floors';

@Component({
  selector: 'app-section',
  templateUrl: './section.component.html',
  styleUrls: ['./section.component.scss']
})

export class SectionComponent {
  @Input() section: Section

  floors: Floor[] = nf_floor
}
<div class="section">
    <div class="head">
        <p>Номер на этаже / Этаж</p>
        <p class="section-name">{{section.nf_name}}</p>
    </div>    
    <div class="floors">
        <ng-container *ngFor="let floor of floors">
            <app-floor 
            [floor] = floor
            *ngIf="floor.nf_sectionid === section.nf_sectionid"
            ></app-floor>
        </ng-container>
    </div>
</div>
