import { Status } from './../../../data/realtyobjectStatus';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { ContextMenu } from 'src/app/models/contextMenu';

@Component({
  selector: 'app-context-menu',
  templateUrl: './context-menu.component.html',
  styleUrls: ['./context-menu.component.scss']
})
export class ContextMenuComponent {

  @Input() contextMenuItems: ContextMenu[]

  @Output() onContextMenuItemClick: EventEmitter<any> = new EventEmitter<any>();

  status = Status

  statusItems: Status[] = [this.status.sold, this.status.free, this.status.estimation, this.status.booked]
  isDisplayStatusMenu: boolean = false

  onContextMenuClick(event:any, data:any): any {
    this.onContextMenuItemClick.emit({
      event,
      data,
    });
    this.isDisplayStatusMenu = true;
  }  
}
<div 
    class="menu"
    *ngFor="let menuItem of contextMenuItems"
    (click)="onContextMenuClick($event, menuItem.menuEvent)"
>
{{menuItem.menuText}}
</div>
<ng-container
    *ngIf="isDisplayStatusMenu">
    <div
        class="status-items"
        *ngFor="let statusItem of statusItems"
    >
    {{statusItem}}
    </div>
</ng-container>
