import { DataService } from 'src/app/services/data.service';
import { Building } from '../../../models/building';
import { Section } from '../../../models/section';
import { Component, Input } from '@angular/core';
import { nf_section } from 'src/app/data/sections';

@Component({
  selector: 'app-building',
  templateUrl: './building.component.html',
  styleUrls: ['./building.component.scss']
})

export class BuildingComponent {

  @Input() building: Building

  sections: Section[] = this.dataService.sections;

  constructor(private dataService: DataService) {
  }

}
<div class="sections" id="{{ building.nf_buildingid }}">
    <ng-container 
        *ngFor="let section of sections; let i = index"
    >
        <app-section 
            [section]="section"
            [sectionID]="section.nf_sectionid"
            *ngIf="section.nf_buildingid === building.nf_buildingid"
        ></app-section>
    </ng-container>
</div>
