import { Section } from './../../models/section';
import { Component, Input } from '@angular/core';
import { nf_section } from 'src/app/data/sections';
import { Building } from 'src/app/models/building';

@Component({
  selector: 'app-building',
  templateUrl: './building.component.html',
  styleUrls: ['./building.component.scss']
})

export class BuildingComponent {

  @Input() building: Building

  sections: Section[] = nf_section

  visible: boolean = false;
  toggle = ()=> {
    this.visible = !this.visible
  }

  showTab(evt: any, building: any) {
    let sections: any;
    let tablinks: any;

    sections = document.getElementsByClassName('sections');
    for(let i = 0; i < sections.length; i++) {
      sections[i].style.display = 'none'
    }

    tablinks = document.getElementsByClassName('tablink');
    for(let i = 0; i < tablinks.length; i++) {
      tablinks[i].className = tablinks[i].className.replace(' active', '');
    }

    document.getElementById(building)!.style.display = "flex"
    evt.currentTarget!.className += ' active'
  }
}
<div class="building"
(click)="toggle()">
    <button class="tablink"
    (onclick)="showTab($event, 'Здание 1')"
    >{{ building.nf_name | uppercase }}</button>
</div>

<div class="sections" id="{{ building.nf_name }}">
    <ng-container *ngFor="let section of sections">
        <app-section 
        [ngClass]="{invisible: !visible}"
        [section] = section
        *ngIf="section.nf_buildingid === building.nf_buildingid"
        ></app-section>
    </ng-container>
</div>
