import { Floor } from '../../../models/floor';
import { Section } from '../../../models/section';
import { Component, Input } from '@angular/core';
import { nf_floor } from 'src/app/data/floors';

@Component({
  selector: 'app-section',
  templateUrl: './section.component.html',
  styleUrls: ['./section.component.scss']
})

export class SectionComponent {
  @Input() section: Section;
  floors: Floor[] = nf_floor;

  numbersOnFloor: number[];

  countNumbersOnFloor(): number[] {
    let result: number[] = []
    // let floors = this.floors.filter(f => f.nf_sectionid === this.section.nf_sectionid)
    console.log(this.section.nf_sectionid)
    for (let i = 0; i < this.floors[0].nf_maxnumberonfloor; i++) {
      result.push(i + 1)
    }
    return result
  }
  constructor() {
    this.numbersOnFloor = this.countNumbersOnFloor()
  }
}
<div class="section">
    <div class="head">
        <p class="floor-description">№ на этаже / Этаж</p>
        <div class="sub-head">
            <p class="section-name">{{section.nf_name}}</p>
            <div class="sub-head2">
                <div 
                    class="number-on-floor"
                    *ngFor="let number of numbersOnFloor"
                >{{number}}</div>
            </div>
        </div>
    </div>    
    <div class="floors">
        <ng-container *ngFor="let floor of floors">
            <app-floor 
            [floor] = floor
            *ngIf="floor.nf_sectionid === section.nf_sectionid"
            ></app-floor>
        </ng-container>
    </div>
</div>
