import { Project } from './../../../models/project';
import { Component, OnChanges, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { nf_building } from 'src/app/data/buildings';
import { nf_project } from 'src/app/data/projects';
import { Building } from 'src/app/models/building';
import { FilterServiceService } from 'src/app/services/filter-service.service';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.scss']
})

export class FilterComponent implements OnInit, OnChanges {

  projectList: Project[] = []
  buildingList: Building[]


  projects: Project[] = nf_project;
  buildings: Building[] = nf_building;

  filterByProjects: Building[]

  projectDropdownList: any;
  buildingDropdownList: any;
  projectDropdownSettings: any;
  buildingDropdownSettings: any;
  form: FormGroup;
  private readonly svc: FilterServiceService;


  constructor(private formBuilder: FormBuilder, svc: FilterServiceService) {
    this.svc = svc
  }
  ngOnChanges(): void {

  }

  ngOnInit(): void {
    this.initForm();
    this.projectDropdownList = this.getProjectData();
    this.buildingDropdownList = this.getBuildingData();
    this.projectDropdownSettings = {
      singleSelection: false,
      idField: 'nf_projectid',
      textField: 'nf_name',
      selectAllText: 'Выбрать все',
      unSelectAllText: 'Отменить выбор'
    };
    this.buildingDropdownSettings = {
      singleSelection: false,
      idField: 'nf_buildingid',
      textField: 'nf_name',
      selectAllText: 'Выбрать все',
      unSelectAllText: 'Отменить выбор'
    };

    this.svc.filteredByProjects$.subscribe((filteredByProjects: Building[]) => this.filterByProjects = filteredByProjects);

  }

  initForm() {
    this.form = this.formBuilder.group({
      projects: ['', [Validators.required]],
      buildings: ['', [Validators.required]]
    })
  }

  getProjectData(): Array<any>{
    return this.projects;
  }

  getBuildingData(): Array<any>{
    if (Array.isArray(this.filterByProjects)) {
      return this.filterByProjects;
    }
    console.log(this.filterByProjects)
    return this.buildings;

  }

  onProjectSelect($event: any) {
    this.projectList = this.form.value.projects
    this.svc.filterProjects(this.projectList);

  }

  handleButtonClick(){
    this.buildingList = this.form.value.buildings;
    this.svc.filterBuildings(this.buildingList)
  }
}

<div class="btn-div">
    <button class="apply-btn"
    (click)="handleButtonClick()"
    >
        <img src="../assets/icons/search_icon.svg" [ngStyle]="{'max-width': '15px', 'margin-right': '4px' }"> 
        Применить</button>    
</div>

<div class="filter-div">
    <form [formGroup]="form" class="input">
        <ng-multiselect-dropdown formControlName="projects"
        [placeholder]="'Проекты'"
        [settings]="projectDropdownSettings"
        [data]="projectDropdownList"
        (onSelect)="onProjectSelect($event)"
        >
        </ng-multiselect-dropdown>
        <ng-multiselect-dropdown formControlName="buildings"
        [placeholder]="'Здания'"
        [settings]="buildingDropdownSettings"
        [data]="buildingDropdownList"
        >
        </ng-multiselect-dropdown>
    </form>
</div>
