import { Component, Input } from '@angular/core';
import { Status } from 'src/app/data/realtyobjectStatus';

@Component({
  selector: 'app-status-menu',
  templateUrl: './status-menu.component.html',
  styleUrls: ['./status-menu.component.scss']
})
export class StatusMenuComponent {

  @Input() statusItems: Status[]

}
<div
    class="status-items"
    *ngFor="let statusItem of statusItems"
>
{{statusItem}}
</div>
