import { Realtyobject } from './../../models/realtyobject';
import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-realtyobject',
  templateUrl: './realtyobject.component.html',
  styleUrls: ['./realtyobject.component.scss']
})
export class RealtyobjectComponent {

 @Input() realtyobject: Realtyobject

}
<div 
    class="realtyobject"
    ngClass="{
        realtyobject.nf_status === 'Продан': {background: red}
    }"
>
    <tr>
        <td>{{ realtyobject.nf_name }}</td>
        <td [ngStyle]="{'width': '50%','text-align':'right'}">{{ realtyobject.nf_status }}</td>
    </tr>
    <tr>
        <td>{{ realtyobject.nf_area }}</td>
        <td [ngStyle]="{'width': '50%','text-align':'right'}">{{ realtyobject.nf_price }}</td>
    </tr>
    <tr>
        <td></td>
        <td [ngStyle]="{'width': '50%','text-align':'right'}">{{ realtyobject.nf_amount}}</td>
    </tr>
    <tr>
        <td></td>
        <td></td>
    </tr>
</div>
