import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Project } from '../models/project';
import { Floor } from '../models/floor';

@Injectable({
  providedIn: 'root'
})
export class DataService {

  projects: Project[];
  floors: Floor[];

  constructor(private http: HttpClient) {
    this.download().subscribe(projects => {
      this.projects = projects
    })


    this.downloadFloor().subscribe(floors => {
      this.floors = floors
    })
  }

  download(): Observable<Project[]> {
    return this.http.get<Project[]>('http://localhost:64382/api/projects')
  }

  downloadFloor(): Observable<Floor[]> {
    return this.http.get<Floor[]>('http://localhost:64382/api/floors')
  }
}
