import { Realtyobject } from './../../models/realtyobject';
import { Floor } from './../../models/floor';
import { Component, Input } from '@angular/core';
import { nf_realtyobject } from 'src/app/data/realtyobjects';

@Component({
  selector: 'app-floor',
  templateUrl: './floor.component.html',
  styleUrls: ['./floor.component.scss']
})

export class FloorComponent {
  @Input() floor: Floor

  realtyobjects: Realtyobject[] = nf_realtyobject

}
<div class="floor">
    <p [ngStyle]="{'margin': '0px', 'width': '80px', 'text-align': 'center', 'border': '1px solid gray'}">{{floor.nf_name}}</p>
    <div class="realtyobjects">
        <ng-container *ngFor="let realtyobject of realtyobjects">
            <app-realtyobject 
                [realtyobject] = realtyobject
                *ngIf="realtyobject.nf_floorid === floor.nf_floorid"
            ></app-realtyobject>
        </ng-container>
    </div>
</div>
