import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.scss']
})

export class FilterComponent implements OnInit {

  dropdownList: any;
  dropdownSettings: any;
  form: FormGroup;

  constructor(private formBuilder: FormBuilder ) {}

  ngOnInit(): void {
    this.initForm();
    this.dropdownList = this.getData();
    this.dropdownSettings = {
      singleSelection: false,
      idField: 'item_id',
      textField: 'item_text',
      selectAllText: 'Select All'
    };
  }

  initForm() {
    this.form = this.formBuilder.group({
      grocery: ['', [Validators.required]]
    })
  }

  handleButtonClick(){
    console.log('reactive form value', this.form.value)
  }

  onItemSelect($event: any) {
    console.log('$event is ', $event);
  }

  getData(): Array<any>{
    return [
      {item_id: 1, item_text: 'Apple', group: 'F'},
      {item_id: 2, item_text: 'Orange', group: 'F'},
      {item_id: 3, item_text: 'Potatoes', group: 'V'},
      {item_id: 4, item_text: 'Cabbage', group: 'V'},
      {item_id: 5, item_text: 'Cauliflower', group: 'V'},
    ];
  }



}

<div class="btn-div">
    <button class="apply-btn"
    (click)="handleButtonClick()"
    >
        <img src="../assets/icons/search_icon.svg" [ngStyle]="{'max-width': '15px', 'margin-right': '4px' }"> 
        Применить</button>    
</div>

<div class="filter-div">
    <form [formGroup]="form"
    class="input">
        <ng-multiselect-dropdown formControlName="grocery"
        class="select"
        [placeholder]="'Проекты'"
        [settings]="dropdownSettings"
        [data]="dropdownList"
        (onSelect)="onItemSelect($event)"
        >
        </ng-multiselect-dropdown>
        <ng-multiselect-dropdown formControlName="grocery"
        class="select"
        [placeholder]="'Здания'"
        [settings]="dropdownSettings"
        [data]="dropdownList"
        (onSelect)="onItemSelect($event)"
        >
        </ng-multiselect-dropdown>
    </form>
</div>
