import { Floor } from '../../../models/floor';
import { Section } from '../../../models/section';
import { Component, Input } from '@angular/core';
import { nf_floor } from 'src/app/data/floors';

@Component({
  selector: 'app-section',
  templateUrl: './section.component.html',
  styleUrls: ['./section.component.scss']
})

export class SectionComponent {
  @Input() section: Section;
  @Input() index: number
  floors: Floor[] = nf_floor;

  numbersOnFloor: number[];

  countNumbersOnFloor(): number[] {
    let result: number[] = []
    let floors = this.floors.filter(f => f.nf_sectionid === 'P1B1S1')
        console.log(this.index)
       for (let i = 0; i < floors[0].nf_maxnumberonfloor; i++) {
      result.push(i + 1)
    }
    return result
  }
  constructor(index: number) {
    this.index = index
    this.numbersOnFloor = this.countNumbersOnFloor()
  }
}
