import { Building } from '../../../models/building';
import { nf_building } from '../../../data/buildings';
import { Component } from '@angular/core';

@Component({
  selector: 'app-project',
  templateUrl: './project.component.html',
  styleUrls: ['./project.component.scss']
})

export class ProjectComponent {


  buildings: Building[] = nf_building

  filteredBuildings: Building[] = [] 

  showTab(evt: any, buildingName: string): void {
    let sections: any = document.getElementsByClassName('sections');
    for(let i = 0; i < sections.length; i++) {
      sections[i].style.display = 'none'
    }

    let tablinks: any = document.getElementsByClassName('tablink');
    for(let i = 0; i < tablinks.length; i++) {
      tablinks[i].className = tablinks[i].className.replace(' active', '');
    }

    document.getElementById(buildingName)!.style.display = "flex"
    evt.currentTarget!.className += ' active'
  }
}
<div class="project">
    <div class="buttons">
        <button class="collapse-button"><img src="../assets/icons/eq_icon.png" [ngStyle]="{'max-width': '20px', 'margin-right': '4px' }"></button>
        <button class="scroll-button" (click)="showFilter()"><</button>
        <button class="scroll-button">></button>
        <div>
            <button
                class="tablink"
                *ngFor="let building of filteredBuildings"
                (click)="showTab($event, building.nf_buildingid)"
            >{{ building.nf_name | uppercase }}</button>
        </div>
    </div>
    <app-building
        *ngFor="let building of filteredBuildings" 
        [building] = "building"
    ></app-building>
</div>
