import { Component } from '@angular/core';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.scss']
})

export class FilterComponent {

}

<div class="btn-div">
    <button class="apply-btn">
        <img src="../assets/icons/search_icon.svg" [ngStyle]="{'max-width': '15px', 'margin-right': '4px' }"> 
        Применить</button>    
</div>

<div class="filter-div">
    <select class="input">
        <option [ngValue]="null">Проекты</option>
    </select>
    <select class="input">Здания</select>
</div>
