import { Component } from '@angular/core';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.scss']
})
export class FilterComponent {

}

<div class="btn-div">
    <button class="apply-btn">
        <img src="../assets/icons/search_icon.svg" [ngStyle]="{'max-width': '15px'}"> 
            Применить</button>    
</div>

<div class="filter-div">
    <input class="input" type="filter" placeholder="Проекты">
    <input class="input" type="filter" placeholder="Здания">
</div>
