import { DataService } from 'src/app/services/data.service';
import { Realtyobject } from '../../../models/realtyobject';
import { Floor } from '../../../models/floor';
import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-floor',
  templateUrl: './floor.component.html',
  styleUrls: ['./floor.component.scss']
})

export class FloorComponent {
  @Input() floor: Floor;

  realtyobjects: Realtyobject[] = this.dataService.realtyobjects;

  constructor(private dataService: DataService) {
  }
}
<div class="floor">
    <p class="floor-name">{{floor.nf_name}}</p>
    <div class="realtyobjects">
        <ng-container *ngFor="let realtyobject of realtyobjects">
            <app-realtyobject 
                [realtyobject] = realtyobject
                *ngIf="realtyobject.nf_floorid === floor.nf_floorid"
            ></app-realtyobject>
        </ng-container>
    </div>
</div>
